		 @Query(value = "          WITH TerceroUnico AS (                                                                                     "          
				 + "              SELECT *,                                                                                              "          
				 + "                     ROW_NUMBER() OVER (PARTITION BY idLocal, CC_Nit ORDER BY nombreTercero) AS filaUnicaCliente     "          
				 + "              FROM bdaquamovil.dbo.tblTerceros                                                                       "          
				 + "          ),                                                                                                         "          
				 + "          DebitosPorComprobante AS (                                                                                 "          
				 + "              SELECT                                                                                                 "          
				 + "                  tblDcto.idTipoCpte,                                                                                "          
				 + "                  tblDcto.idCpte,                                                                                    "          
				 + "                  CONVERT(VARCHAR, tblDcto.fechaDcto, 23) AS fechaDcto,                                              "          
				 + "                  tblDcto.idPeriodo,                                                                                 "          
				 + "                  tblTipoCpte.nombreCmpte AS nombreTipoComprobante,                                                  "          
				 + "                  tblDctoDetalle.idCliente,                                                                          "          
				 + "                  SUM(tblDctoDetalle.vrDebito) AS vrTotal                                                            "          
				 + "              FROM BDMailMarketing.dbo.tblDcto                                                                       "          
				 + "              INNER JOIN BDMailMarketing.dbo.tblTipoCpte                                                             "          
				 + "                  ON tblDcto.idTipoCpte = tblTipoCpte.idTipoCpte                                                     "          
				 + "              INNER JOIN BDMailMarketing.dbo.tblDctoDetalle                                                          "          
				 + "                  ON tblDcto.idLocal = tblDctoDetalle.idLocal                                                        "          
				 + "                  AND tblDcto.idCpte = tblDctoDetalle.idCpte                                                         "          
				 + "                  AND tblDcto.idTipoCpte = tblDctoDetalle.idTipoCpte                                                 "          
				 + "              WHERE tblDcto.idLocal = ?1                                                                            "          
				 + "                AND tblDcto.idTipoCpte = ?2                                                                           "          
				 + "                AND tblDcto.idPeriodo IN( ?3, ?4)                                                                       "          
				 + "          	  AND tblDctoDetalle.vrDebito > 0                                                                       "          
				 + "              GROUP BY                                                                                               "          
				 + "                  tblDcto.idTipoCpte,                                                                                "          
				 + "                  tblDcto.idCpte,                                                                                    "          
				 + "                  tblDcto.fechaDcto,                                                                                 "          
				 + "                  tblDcto.idPeriodo,                                                                                 "          
				 + "                  tblTipoCpte.nombreCmpte,                                                                           "          
				 + "                  tblDctoDetalle.idCliente                                                                           "          
				 + "          )                                                                                                          "          
				 + "          SELECT                                                                                                     "          
				 + "              dpc.*,                                                                                                 "          
				 + "              t.nombreTercero AS nombreUsuario                                                                       "          
				 + "          FROM DebitosPorComprobante dpc                                                                             "          
				 + "          LEFT JOIN TerceroUnico t                                                                                   "          
				 + "              ON t.idLocal = ?1                                                                                     "          
				 + "              AND t.CC_Nit = dpc.idCliente COLLATE DATABASE_DEFAULT                                                  "          
				 + "              AND t.filaUnicaCliente = 1	                                                                          "          
				 + "          	ORDER BY idCpte desc, idPeriodo DESC                                                                      ",
	             nativeQuery = true)
		 List<TblDctoDTO> listaComprobantes(int idLocal, int idTipoCpte, int idPeriodo, int idPeriodo2);
		 
		 
		 
