		 @Query(value = "       SELECT DISTINCT tblDcto.idTipoCpte,                                 "                           
					+ "           tblDcto.idCpte,                                                 "                                
					+ "           CONVERT(VARCHAR, tblDcto.fechaDcto, 23) AS fechaDcto,           "                           
					+ "           tblDcto.idPeriodo,                                              "                           		        
					+ "   		MAX(TmpDetalle.idCliente) AS idCliente,                           "                     
					+ "   		TmpDetalle.idCuentaAux,                                           "          
					+ "   		MAX(tblPucAux.nombreCuenta) AS nombreCuenta,                      "                         
					+ "   		MAX(tercero.nombreTercero ) AS nombreTercero,                     "          
					+ "   		TmpDetalle.VrDebitoSolo,                                          "          
					+ "   		TmpDetalle.VrCreditoSolo,                                         "          
					+ "   		 CASE                                                             "                          
					+ "   		 WHEN TmpDetalle.VrDebitoSolo = 0                                 "                    
					+ "   		 THEN TmpDetalle.VrCreditoSolo                                    "                   
					+ "   		 ELSE TmpDetalle.VrDebitoSolo                                     "          
					+ "   		 END AS vrTotal,                                                  "          
				    + "        TmpDetalle.detalle,                                                "          
				    + "        MAX(tblTipoCpte.nombreCmpte) AS tipoComprobante					  "          		 							
					+ "    	FROM BDMailMarketing.dbo.tblDcto   							          "          
					+ "    	INNER JOIN tblTipoCpte                                                "                         
					+ "    	ON tblDcto.idTipoCpte = tblTipoCpte.idTipoCpte                        "          
					+ "    	INNER JOIN                                                            "           
					+ "   	(SELECT idLocal,                                                      "          
                 + "               idCpte,                                                     "          
                 + "               idTipoCpte,                                                 "          
                 + "               MAX(idCliente) AS idCliente,                                "          
                 + "               idCuentaAux,                                                "          
                 + "               SUM(vrDebito) AS VrDebitoSolo,                              "          
                 + "               SUM(vrCredito) AS VrCreditoSolo,                            "          
                 + "               MAX(observacion) AS detalle                                 "          
                 + "        FROM tblDctoDetalle                                                "          
                 + "        WHERE idLocal = ?1                                                 "                                          
                 + "        AND idCuentaAux IN ?3                                              "          
                 + "       GROUP BY idLocal, idCpte, idTipoCpte, idCuentaAux                   "          
                 + "        ) TmpDetalle                                                       "          
                 + "       ON tblDcto.idLocal = TmpDetalle.idLocal                             "          
                 + "       AND tblDcto.idCpte = TmpDetalle.idCpte                              "          
                 + "       AND tblDcto.idTipoCpte = TmpDetalle.idTipoCpte                      "          
					+ "   	INNER JOIN tblPucAux                                                  "                         
					+ "   	ON TmpDetalle.idLocal = tblPucAux.idLocal                             "                     
					+ "   	AND TmpDetalle.idCuentaAux = tblPucAux.idCuentaAux   				  "          			
					+ "   	INNER JOIN bdaquamovil.dbo.tblTerceros tercero                        "                         
					+ "   	ON TmpDetalle.idLocal = tercero.idLocal                               "                     
                 + "       AND TmpDetalle.idCliente = tercero.CC_Nit COLLATE DATABASE_DEFAULT  "             							
					+ "    	WHERE tblDcto.idLocal = ?1                                            "                                     
					+ "    	AND tblDcto.idPeriodo = ?2                                            "                    
					+ "   	AND TmpDetalle.idCuentaAux IN ?3                                      "                     
					+ "    	GROUP BY tblDcto.idTipoCpte,                                          "                         
					+ "             tblDcto.idCpte,                                               "                             
					+ "             tblDcto.fechaDcto,                                            "                           
					+ "             tblDcto.idPeriodo,                                            "                           
					+ "             TmpDetalle.idCuentaAux,	                                      "          
					+ "   		  TmpDetalle.VrDebitoSolo,                                        "          
					+ "   		  TmpDetalle.VrCreditoSolo,                                       "          
					+ "   		  TmpDetalle.detalle                                              "          
					+ "   		  ORDER BY idCliente desc                                         ",
	             nativeQuery = true)
		    List<TblDctoDTO> listaMovimientoPorAuxiliar(int idLocal, int idPeriodo, List<Integer> cuentasContables);
		 
		 
		 
